macrocode:
[h: input("Modifier")]
[h: Die_1 = 1d20]
[h: Die_2 = 1d20]
[h, if(Die_1 >= Die_2), CODE:{
	[h: Dice = Die_1]
};{
	[h: Dice = Die_2]
}]
<b><font color="Orange">2d20 + [r: Modifier] (adv)</font></b> => <font color="Gray">([r: Die_1] vs [r: Die_2])</font> => [t: Dice + Modifier - Exhaustion]
<font color="Green">[r,if(Dice==20): ", Crit!"]</font>
<font color="Red">[r,if(Dice==1): ", Critfail! (That's 1 in 400)"]</font>

